import  React, { useState } from  'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

const CreateNote = (props) => {
    const [expand, setExpand] = useState(false);    //write  note click show title and button
    const [note, setNote] = useState({
        title:'',
        content:'',

    });

    const InputEvent = (event) => {
        // const value = event.target.value;
        // const name = event.target.name;
        const {name, value} = event.target;       //object destructring
        setNote((prevData)=> {
            return(
                {...prevData,                       //spreed operator
                 [name]: value,
                }
            );
        });
    }
        const addEvent = () => {
            props.passNote(note);   
            setNote({
                title:'',
                content:'',

            });



    };
    const expandIt = () => {                //use onclick show title and button
        setExpand(true);
    }
    const btoNormal = () => {               //use double click back to real condition
        setExpand(false);
    }

        return(
            <>
             <div className='main_note'  onDoubleClick={btoNormal}>
               <form>
             { expand?                          //use ternary operator  show title button
                   <input 
                   type="text" 
                   name='title'
                   value={note.title}
                    onChange={InputEvent} 
                    laceholder="Title" 
                    autoComplete='off'
                     /> : null }
                   <textarea 
                   rows="" 
                   column="" 
                   name='content'
                   value={note.content}
                   onChange={InputEvent}  
                   placeholder="Write a note..."
                   onClick={expandIt}

                   >
                   </textarea>
                    { expand?
                   <Button onClick={addEvent}>
                        <AddIcon className='plus_sign'/>
                   </Button> : null }
               </form>

             </div>
            </>
        )
    }

    export default CreateNote;
