import  React, { useState } from  'react';
import Header from './Header';
import CreateNote from './CreateNote';
import Note from './Note';
import Footer from './Footer';

const App = () => {
    const [addItem, setAddItem] = useState([]);      //call value in note use empty array usestate

    const addNote = (note) => {     //also pass here note
        //alert("I am clicked");
        setAddItem((prevData) => {
            return[...prevData, note];
        });
    };
    const onDelete = (id) => {               //use for delete particular id 
        setAddItem((olddata)=> 
        olddata.filter((currdata, indx) => {     //filter use for only id dlete other show item
            return indx !== id;
        })
        )          
    };




    return(
        <>
           <Header/>
           {/* use props */}
           <CreateNote  passNote = {addNote} />       

           {addItem.map((val, index) => {
               return( <Note 
                   key={index}
                   id={index}
                   title={val.title}
                  content={val.content}
                  deleteItem= {onDelete}
               />

               )

           })}
           <Footer/> 

        </>
    )
}

export default App;
